import {Container, Navbar, Button, Form} from 'react-bootstrap'
import { ethers } from "ethers";
import React, {useState, useEffect} from 'react'

import token_abi from './contracts/sct_abi.json'

const provider = new ethers.providers.AlchemyProvider('maticmum', 'eytwgLayNf_PqhNiSwaOPzkb-fS9hss5');

const defaultUserWalletState = {
  address: '0x0000000000000000000000000000000000000000',
  network: null,
  connected: false
}

const mumbaiChainParameter = {
  chainId: "0x13881",
  chainName: "Mumbai",
  nativeCurrency: {
    name: "MATIC",
    symbol: "MATIC",
    decimals: 18,
  },
  rpcUrls: ["https://rpc-mumbai.maticvigil.com"],
  blockExplorerUrls: ["https://mumbai.polygonscan.com/"],
}


function App() {

































  // Connect to MetaMask Start //

  const [errorMessage, setErrorMessage] = useState(null);
	const [defaultAccount, setDefaultAccount] = useState(null);
	const [connButtonText, setConnButtonText] = useState('Connect Wallet');

    const connectWalletHandler = () => {
        if (window.ethereum && window.ethereum.isMetaMask) {

            window.ethereum.request({ method: 'eth_requestAccounts'})
            .then(result => {
                accountChangedHandler(result[0]);
                setConnButtonText('Wallet Connected');
            })
            .catch(error => {
                setErrorMessage(error.message);

            });

        } else {
            console.log('Need to install MetaMask');
            setErrorMessage('Please install MetaMask browser extension to interact');
        }
    }
    const accountChangedHandler = (newAccount) => {
      setDefaultAccount(newAccount);
  }

  // Connect to MetaMask End //

  return (
    <div className="App">
      <Navbar bg="light">
        <Container>
          <Navbar.Brand>SCT TESTE</Navbar.Brand>
          <Navbar.Text><Button variant="outline-dark" onClick={connectWalletHandler}>{connButtonText}</Button></Navbar.Text>
        </Container>
      </Navbar>
      <div className="pt-5">
        <h4 className="py-5">Token Name:</h4>
        <h4 className="py-5">Token Symbol:</h4>
        <h4 className="py-5">Decimals:</h4>
        <h4 className="py-5">Total Suply:</h4>
      </div>
    </div>
  );
}

export default App;
