import {Container, Navbar, Button, Form} from 'react-bootstrap'
import { ethers } from "ethers";
import React, {useState, useEffect} from 'react'
import { useConnect } from 'wagmi'
import toast, { Toaster } from "react-hot-toast";
import { isMobile } from "react-device-detect";
import sct_abi from './contracts/sct_abi.json'

const url = "https://polygon-mumbai.g.alchemy.com/v2/LDruTtddIy66V4Lr7gEXbQgjBTdBCHzL";

const defaultUserWalletState = {
  address: '0x0000000000000000000000000000000000000000',
  network: null,
  connected: false
}

const mumbaiChainParameter = {
  chainId: "0x13881",
  chainName: "Mumbai",
  nativeCurrency: {
    name: "MATIC",
    symbol: "MATIC",
    decimals: 18,
  },
  rpcUrls: ["https://rpc-mumbai.maticvigil.com"],
  blockExplorerUrls: ["https://mumbai.polygonscan.com/"],
}
function App() {

  // Connect MetaMask Mumbai Start End //

  let [isLoading, setIsLoading] = React.useState(false);

  let [userWallet, setUserWallet] = React.useState(defaultUserWalletState);

  let [buttonText, setButtonText] = React.useState('Connect Wallet')

  const [{
    data: connectData,
    loading: connectDataLoading,
    error: connectDataError
  }, connect] = useConnect();

  let wagmiObj;
  connectData.connectors.map(obj => { wagmiObj = obj });

  React.useEffect(() => {
    if (typeof window.ethereum !== 'undefined') {
      window.ethereum.on('accountsChanged', (accounts) => {
        console.log(`Wallet changed: ${accounts[0]}`);
        if (accounts[0] === undefined) {
          setUserWallet(defaultUserWalletState);
        }
        else if (accounts[0] !== userWallet.address) {
          setUserWallet(prevUserWallet => ({
            ...prevUserWallet,
            address: accounts[0]
          }));
        }
      })
      window.ethereum.on('chainChanged', (chainId) => {
        console.log(`Network changed: ${chainId}`);
        if (chainId !== '0x13881') {
          window.location.reload();
        }
      })
    } else {
      toast('Please install Metamask to use Solid World Dapp.');
    }
  }, []);

  async function handleWalletConnection() {
    setIsLoading(true);
    if (!userWallet.connected) {
      try {
        setButtonText("Wallet Connected");
        console.log('Connecting MetaMask...');
        const connectMetamask = await connect(wagmiObj);
        if (connectMetamask.data) {
          if (window.ethereum.chainId !== '0x13881') { 
            await checkNetwork();
          }
          setUserWallet(prevUserWallet => ({
            ...prevUserWallet,
            network: window.ethereum.chainId,
            address: connectMetamask.data.account,
            connected: true
          }));
          toast.success('Connected Account to Solid World Dapp.', {duration: 3000});
          console.log('Connected Account to Mumbai Network: ', connectMetamask.data.account);
        }
        setIsLoading(false);
        return;
      } catch (error) {
        setUserWallet(defaultUserWalletState);
        console.log('Connecting MetaMask Error: ', error, connectDataError);
        setIsLoading(false);
        window.location.reload();
        return;
      }
    } else {
      setUserWallet(defaultUserWalletState);
      toast.error('Disconnected Account.', { duration: 3000 });
      console.log('User wallet disconnect');
      setIsLoading(false);
      return;
    }
  }

  async function checkNetwork() {
    console.log('Connecting MetaMask to Mumbai Network...');
    toast.loading('Please connect your Metamask to Mumbai Polygon Testnet to use Solid World Dapp');
    try {
      await window.ethereum.request({
        method: 'wallet_switchEthereumChain',
        params: [{ chainId: '0x13881' }],
      });
      return;
    } catch (switchError) {
      if (isMobile || switchError.code === 4902) {
        try {
          await window.ethereum.request({
            method: 'wallet_addEthereumChain',
            params: [
              mumbaiChainParameter
            ],
          });
          return;
        } catch (addError) {
          console.log('Connecting Mumbai Network: ', addError.message);
          throw addError;
        }
      }
      console.log('Connecting Mumbai Netowrk: ', switchError.message);
      throw switchError;
    }
  }

// Connect MetaMask Mumbai Netowrk End//

// Contract Connection //
const [provider, setProvider] = useState(null)
const [signer, setSigner] = useState(null);
const [contract, setContract] = useState(null);
const [name, setName] = useState(null);
const [symbol, setSymbol] = useState(null);
const [decimals, setDecimals] = useState(null);
const [totalSuply, setTotalSuply] = useState(null);

const contractAddress = '0x2C963ebaFCF40016AE7Ef5B3236a480de4Dc491B'

const updateContract = () => {
  let tempProvider = new ethers.providers.JsonRpcProvider(url);

  let tempSigner = provider.getSigner()
  setSigner(tempSigner);
  let tempContract = new ethers.Contract(contractAddress, sct_abi, tempSigner)
  setContract(tempContract);

} 
const infos = async () => {
  let tempSymbol = await contract.symbol();
  setSymbol(tempSymbol)
  let tempDecimals = await contract.decimals();
  setDecimals(tempDecimals)
  let tempTotalSuply = await contract.totalSuply();
  setTotalSuply(tempTotalSuply)
}

function connectWallet(){
    handleWalletConnection(),
    updateContract()
}



return (
    <div className="App">
      <Navbar bg="light">
        <Container>
          <Navbar.Brand>SCT TESTE</Navbar.Brand>
          <Navbar.Text><Button variant="outline-dark" onClick={connectWallet}>{buttonText}</Button></Navbar.Text>
        </Container>
      </Navbar>
      <div className="pt-5">
        <h4 className="py-5">Token Name: {name}</h4>
        <h4 className="py-5">Token Symbol: {symbol} </h4>
        <h4 className="py-5">Decimals: {decimals} </h4>
        <h4 className="py-5">Total Suply: {totalSuply}</h4>
      </div>
    </div>
  );
}

export default App;
