import {Container, Navbar, Button, Form} from 'react-bootstrap'
import { ethers } from "ethers";
import React, {useState, useEffect} from 'react'

import token_abi from './contracts/sct_abi.json'

function App() {

  // Connect to MetaMask Start //

  const [errorMessage, setErrorMessage] = useState(null);
	const [defaultAccount, setDefaultAccount] = useState(null);
	const [connButtonText, setConnButtonText] = useState('Connect Wallet');

    const connectWalletHandler = () => {
        if (window.ethereum && window.ethereum.isMetaMask) {

            window.ethereum.request({ method: 'eth_requestAccounts'})
            .then(result => {
                accountChangedHandler(result[0]);
                setConnButtonText('Wallet Connected');
            })
            .catch(error => {
                setErrorMessage(error.message);

            });

        } else {
            console.log('Need to install MetaMask');
            setErrorMessage('Please install MetaMask browser extension to interact');
        }
    }
    const accountChangedHandler = (newAccount) => {
      setDefaultAccount(newAccount);
      updateContract()
  }

  // Connect to MetaMask End //

  // Sct Contract Start 

  const sctAddress = "0x54005ab145e74d6354fe81390523e67dc40da64f"
  const [provider, setProvider] = useState(null)
  const [signer, setSigner] = useState(null)
  const [contract, setContract] = useState(null)

  const updateContract = () => {

    let tempProvider = new ethers.providers.Web3Provider('maticmum');
    setProvider(tempProvider);

    let tempSigner = provider.getSigner();
    setSigner(tempSigner);

    let tempContract = new ethers.Contract(sctAddress, token_abi, signer);
    setContract(tempContract);
  }

  //

  // Sct Infos Start //

  const [name, setName] = useState(null)
  const [symbol, setSymbol] = useState(null)
  const [decimals, setDecimals] = useState(null)
  const [totalSupply, setTotalSupply] = useState(null)

  const updateName = async () => {
    let tempName = await contract.name()
    setName(tempName)
  }

  const updateSymbol = async () => {
    let temSymbol = await contract.symbol()
    setSymbol(temSymbol)
  }

  const updateDecimals = async () => {
    let tempDecimals = await contract.decimals()
    setDecimals(tempDecimals)
  }

  const updateTotalSupply = async () => {
    let tempTotalSupply = await contract.totalSupply()
    setTotalSupply(tempTotalSupply)
  }

  // Sct Infos End //

  // Sct Load Infos Start //

  useEffect(() => {
    if(contract != null) {
      updateName();
      updateSymbol();
      updateDecimals();
      updateTotalSupply();  
    }
  }, [contract])

  // Sct Load Infos End //
  return (
    <div className="App">
      <Navbar bg="light">
        <Container>
          <Navbar.Brand>SCT TESTE</Navbar.Brand>
          <Navbar.Text><Button variant="outline-dark" onClick={connectWalletHandler}>{connButtonText}</Button></Navbar.Text>
        </Container>
      </Navbar>
      <div className="pt-5">
        <h4 className="py-5">Token Name: {name} </h4>
        <h4 className="py-5">Token Symbol: {symbol} </h4>
        <h4 className="py-5">Decimals: {decimals} </h4>
        <h4 className="py-5">Total Suply: {totalSupply} </h4>
      </div>
    </div>
  );
}

export default App;
